import React, { useEffect, useState } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { Typewriter } from 'react-simple-typewriter';

function App() {
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    AOS.init({ duration: 900, once: true, offset: 40 });
  }, []);

  return (
    <>
      {/* THEME + GLOBAL */}
      <style>{`
:root {
  /* Navigation */
  --nav-bg: #A7D3C6;              /* Soft mint navbar background */
  --nav-ink: #0B4B3A;             /* Deep forest navbar text/buttons */
  --nav-h: 88px;                  /* Header height (mobile) */

  /* Accent & Brand Colors */
  --accent: #2B8F78;              /* Deep, formal mint accent */
  --ring: rgba(43, 143, 120, 0.25); /* Accent-colored ring/shadow */

  /* Text Colors */
  --ink: #0B4B3A;                 /* Primary body text */
  --muted: #5B6776;               /* Muted/secondary text */

  /* Backgrounds */
  --body: #1D2732;                /* Main page background */
  --card: #FFFFFF;                /* Card backgrounds */
}

@media (min-width:768px){
  :root{ --nav-h:96px; } /* header height (desktop) */
}

html { scroll-behavior: smooth; }
body, #root { background:#F6F7F8; color:#111; overflow-x:hidden; }
img, video { max-width:100%; height:auto; }
.shadow-soft { box-shadow: 0 6px 18px rgba(0,0,0,.08); }

/* keep anchor jumps below fixed navbar (no global padding on main) */
[id]{ scroll-margin-top: calc(var(--nav-h) + 10px); }

/* --- Decorative fade band behind sections (mint) --- */
.section-shell{ position:relative }
.section-shell::before{
  content:"";
  position:absolute; inset:-2rem -9999px auto -9999px; height:220px;
  background:linear-gradient(180deg, rgba(43,143,120,.08), transparent 70%);
  pointer-events:none;
}

/* Headings / badges */
.eyebrow{
  display:inline-block; font-size:.8rem; letter-spacing:.12em; text-transform:uppercase;
  color:var(--muted); background:rgba(43,143,120,.12); border:1px solid var(--ring);
  padding:.4rem .6rem; border-radius:999px;
}
.section-title{
  font-family:'Playfair Display', serif; line-height:1.1;
  color:var(--accent); font-weight:800; margin:.75rem 0 1rem;
}
.gold-hr{
  height:3px; width:72px; background:var(--accent); border-radius:3px; margin:.75rem 0 1.25rem;
}
.lede{ font-weight:600; color:var(--body); }
.copy{ color:#223041; font-size:1.05rem; }

.badges{ display:flex; flex-wrap:wrap; gap:.5rem .6rem; margin:.35rem 0 1rem; }
.badge{
  background:#fff; border:1px solid var(--ring); padding:.45rem .6rem; border-radius:10px;
  font-size:.9rem; color:#111; box-shadow:0 8px 24px rgba(0,0,0,.06);
}

/* Standalone "About" block */
.about-block{
  background:transparent;
  border-left:4px solid var(--accent);
  padding:.25rem 0 .25rem 1rem;
  margin: .5rem 0 1.25rem;
}
.about-block h5{
  margin:0 0 .4rem; font-weight:700; font-size:1rem; color:#0f172a;
}
.about-block p{ margin:0; color:#223041; font-size:1.05rem; }

/* Overview content cards */
.content-grid{
  display:grid; grid-template-columns: 1fr; gap:1rem; margin-top:.25rem;
}
@media(min-width:768px){
  .content-grid{ grid-template-columns: 1fr 1fr; }
}
.content-card{
  background:#fff; border:1px solid var(--ring); border-radius:14px;
  padding:1rem 1.1rem; box-shadow:0 10px 24px rgba(0,0,0,.06);
  position:relative;
}
.content-card h5{
  font-weight:700; font-size:.95rem; color:#0f172a; margin:0 0 .75rem;
}

/* stat pills inside cards */
.stat-row{ display:grid; grid-template-columns: 1fr; gap:.6rem; }
@media(min-width:520px){
  .stat-row{ grid-template-columns: 1fr 1fr; }
}
.stat{
  border:1px solid var(--ring); border-radius:10px; padding:.6rem .7rem; background:#fff;
}
.stat .t{ font-weight:700; }
.stat .s{ font-size:.9rem; color:#4b5563; line-height:1.5; }

/* Apartment Mix: wider spacing */
.mix-row{
  display:grid;
  grid-template-columns:1fr;
  gap:1rem;
}
@media(min-width:520px){
  .mix-row{ grid-template-columns:1fr 1fr; }
}
.mix-stat{
  padding:1rem 1.1rem;
}
.mix-stat .t{
  font-size:1.05rem;
  font-weight:800;
  color:#0f172a;
  margin-bottom:.35rem;
}

/* compact checklist columns */
.check-cols{ display:grid; grid-template-columns: 1fr; gap:.35rem 1rem; }
@media(min-width:640px){
  .check-cols{ grid-template-columns: 1fr 1fr; }
}
.check-cols li{
  list-style:none; display:flex; gap:.5rem; align-items:flex-start; color:#213044;
}
.check-cols li::before{
  content:""; width:.55rem; height:.55rem; margin-top:.42rem; flex:0 0 .55rem;
  border-radius:2px; background:var(--accent); box-shadow:0 0 0 3px var(--ring);
}

/* image stack (right column) */
.image-stack{ display:flex; flex-direction:column; gap:1rem; }
.image-card{
  background:var(--card); border:1px solid var(--ring); border-radius:16px; padding:.6rem;
  box-shadow:0 20px 40px rgba(0,0,0,.08);
  transition: transform .25s ease, box-shadow .25s ease;
}
.image-card:hover{ transform: rotate(-.3deg) translateY(-2px); box-shadow:0 26px 60px rgba(0,0,0,.12); }
.image-card img{ border-radius:12px; display:block; width:100%; height:auto; }
.image-card.small{ max-width:100%; margin-left:0; transform:none; }
@media(min-width:1024px){
  .image-card.small{ max-width:72%; margin-left:auto; transform:translate(6px,-4px); }
}

/* stamp */
.stamp{
  position:absolute; top:-10px; right:-10px; background:#fff;
  border:2px dashed var(--accent); color:#111; padding:.35rem .55rem; border-radius:10px;
  font-weight:800; font-size:.7rem; transform:rotate(6deg);
  box-shadow:0 6px 16px rgba(0,0,0,.08);
}

/* === WHY CHOOSE (Pedigree + Perks band) === */
.why-band{
  background: linear-gradient(180deg, rgba(43,143,120,.06), rgba(43,143,120,.02));
  border-top: 4px solid var(--accent);
  border-bottom: 1px solid var(--ring);
}
.why-wrap{ max-width: 72rem; margin: 0 auto; padding: 2.5rem 1.5rem; }
.why-head{
  text-align:center; margin:0 0 1.25rem;
  font-family:'Playfair Display', serif; font-weight:800; color:var(--accent);
}
.why-copy{ color:#213044; text-align:center; max-width:56rem; margin:.35rem auto 1.25rem; }

/* --- Pedigree marquee --- */
.pedigree-marquee{
  position:relative;
  overflow:hidden;
  margin: .25rem auto 2rem;
  padding: 2px 0;
  -webkit-mask-image: linear-gradient(90deg, transparent 0, #000 6%, #000 94%, transparent 100%);
          mask-image: linear-gradient(90deg, transparent 0, #000 6%, #000 94%, transparent 100%);
}
.pedigree-track{
  display:flex;
  gap:.65rem;
  width:max-content;
  will-change: transform;
  animation: pedigree-scroll 24s linear infinite;
}
.pedigree-marquee:hover .pedigree-track{ animation-play-state: paused; }

@keyframes pedigree-scroll{
  from{ transform: translateX(0); }
  to  { transform: translateX(-50%); }
}

/* tag pill */
.tag{
  background:#fff; border:1px solid var(--ring);
  border-radius:999px; padding:.45rem .75rem;
  font-weight:600; font-size:.9rem; white-space:nowrap;
  box-shadow:0 6px 18px rgba(0,0,0,.05);
}

/* Perks grid */
.perks{
  display:grid; grid-template-columns:1fr; gap:14px;
}
@media(min-width:768px){ .perks{ grid-template-columns:1fr 1fr; } }
.perk{
  background:#fff; border:1px solid var(--ring); border-radius:14px;
  padding:14px 16px; box-shadow:0 8px 22px rgba(0,0,0,.06);
}
.perk h4{
  margin:0 0 .35rem; font-weight:800; color:#0f172a; font-size:1.05rem;
}
.perk p{ margin:0; color:#5b6776; line-height:1.55; font-size:.93rem; }

.why-cta{ text-align:center; margin-top:1.25rem; }
.why-cta a{
  display:inline-block; padding:.6rem 1rem; border:1px solid var(--accent);
  border-radius:12px; font-weight:700; color:#0f172a; background:#fff;
  box-shadow:0 8px 18px rgba(0,0,0,.06);
}
.why-cta a:hover{ background:rgba(43,143,120,.08); }

/* --- Company section --- */
.company{
  background:#fff;
  border-top:1px solid var(--ring);
}
.company-wrap{ max-width:72rem; margin:0 auto; padding:2.25rem 1.5rem; }
.company h3{
  text-align:center; color:var(--accent);
  font-family:'Playfair Display', serif; font-weight:800;
  margin:0 0 1rem;
}
.company p{ color:#223041; }
.company-grid{
  display:grid; grid-template-columns:1fr; gap:1rem;
}
@media(min-width:900px){
  .company-grid{ grid-template-columns:1fr 1fr; }
}
.company-card{
  background:#fff; border:1px solid var(--ring); border-radius:14px;
  padding:16px; box-shadow:0 8px 22px rgba(0,0,0,.06);
}
.company-card h4{ margin:0 0 .4rem; font-weight:800; color:#0f172a; }
.company-card ul{ margin:.25rem 0 0; padding:0; list-style:none; }
.company-card li{ display:flex; gap:.5rem; margin:.35rem 0; color:#213044; }
.company-card li::before{
  content:""; width:.55rem; height:.55rem; margin-top:.45rem; flex:0 0 .55rem;
  border-radius:2px; background:var(--accent); box-shadow:0 0 0 3px var(--ring);
}
      `}</style>

      {/* NAVBAR */}
      <header
        className="w-full fixed top-0 left-0 z-50 px-4 md:px-8 py-3 shadow-soft"
        style={{ background: 'var(--nav-bg)', color: 'var(--nav-ink)' }}
      >
        <div className="max-w-7xl mx-auto flex items-center justify-between gap-3">
          <div className="h-12 md:h-14 flex items-center">
            <img
              src="green-logo.png"
              alt="Assotech Windsor Group"
              className="max-h-full w-auto object-contain origin-left scale-[1.4] md:scale-[4.0]"
            />
          </div>

        <nav className="hidden md:flex items-center gap-8 font-semibold uppercase tracking-wide">
          {['overview', 'gallery', 'brochure', 'sitemap', 'contact'].map((link) => (
            <a key={link} href={`#${link}`} className="hover:underline underline-offset-4">
              {link}
            </a>
          ))}
          <div className="text-right leading-tight">
            <div className="text-[11px] opacity-80">Call Us Today!</div>
            <div className="font-bold">+91-75661-55505</div>
          </div>
          <a
            href="https://wa.me/917566155505"
            target="_blank"
            rel="noreferrer"
            className="rounded px-4 py-2 font-bold text-white"
            style={{ background: 'var(--nav-ink)' }}
          >
            📞 Contact Us
          </a>
        </nav>

          <button
            onClick={() => setMenuOpen((v) => !v)}
            className="md:hidden inline-flex items-center justify-center h-10 w-10"
            aria-label="Toggle menu"
          >
            <svg width="26" height="26" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path strokeLinecap="round" strokeLinejoin="round"
                d={menuOpen ? 'M6 18L18 6M6 6l12 12' : 'M3 6h18M3 12h18M3 18h18'} />
            </svg>
          </button>
        </div>

        {menuOpen && (
          <div className="md:hidden mt-3 space-y-3 font-semibold uppercase tracking-wide">
            {['overview', 'gallery', 'brochure', 'sitemap', 'contact'].map((link) => (
              <a key={link} href={`#${link}`} onClick={() => setMenuOpen(false)} className="block" style={{ color: 'var(--nav-ink)' }}>
                {link}
              </a>
            ))}
            <div className="pt-2">
              <div className="text:[11px] opacity-80">Call Us Today!</div>
              <div className="font-bold">+91-75661-55505</div>
            </div>
            <a
              href="https://wa.me/917566155505" target="_blank" rel="noreferrer"
              className="inline-block rounded px-4 py-2 font-bold text-white" style={{ background: 'var(--nav-ink)' }}
            >
              📞 Contact Us
            </a>
          </div>
        )}
      </header>

      {/* PAGE CONTENT */}
      <main>
