import React, { useEffect, useState } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { Typewriter } from 'react-simple-typewriter';

function App() {
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    AOS.init({ duration: 900, once: true, offset: 40 });
  }, []);

  return (
    <>
      {/* THEME + GLOBAL */}
      <style>{`
        :root{
          --nav-bg:#A7D3C6;     /* mint navbar background */
          --nav-ink:#0B4B3A;    /* deep forest navbar text/buttons */
          --accent:#E3B341;     /* gold accent */
          --ink:#0B4B3A;
          --body:#1d2732;
          --muted:#5b6776;
          --card:#ffffff;
          --ring:rgba(227,179,65,.25);
          --nav-h:88px;         /* header height (mobile) */
        }

        @media (min-width:768px){
          :root{ --nav-h:96px; } /* header height (desktop) */
        }

        html { scroll-behavior: smooth; }
        body, #root { background:#F6F7F8; color:#111; overflow-x:hidden; }
        img, video { max-width:100%; height:auto; }
        .shadow-soft { box-shadow: 0 6px 18px rgba(0,0,0,.08); }

        /* ensure anchor jumps land below the fixed navbar */
        [id]{ scroll-margin-top: calc(var(--nav-h) + 10px); }
        main { padding-top: var(--nav-h); }

        /* --- Windsor Overview polish --- */
        .section-shell{ position:relative }
        .section-shell::before{
          content:"";
          position:absolute; inset:-2rem -9999px auto -9999px; height:220px;
          background:linear-gradient(180deg, rgba(227,179,65,.08), transparent 70%);
          pointer-events:none;
        }

        .eyebrow{
          display:inline-block; font-size:.8rem; letter-spacing:.12em; text-transform:uppercase;
          color:var(--muted); background:rgba(227,179,65,.12); border:1px solid var(--ring);
          padding:.4rem .6rem; border-radius:999px;
        }

        .section-title{
          font-family:'Playfair Display', serif; line-height:1.1;
          color:var(--accent); font-weight:800; margin:.75rem 0 1rem;
        }

        .gold-hr{
          height:3px; width:72px; background:var(--accent); border-radius:3px; margin:.75rem 0 1.25rem;
        }

        .lede{ font-weight:600; color:var(--body); }
        .copy{ color:#223041; font-size:1.05rem; }

        .badges{ display:flex; flex-wrap:wrap; gap:.5rem .6rem; margin:.25rem 0 1rem; }
        .badge{
          background:#fff; border:1px solid var(--ring); padding:.45rem .6rem; border-radius:10px;
          font-size:.9rem; color:#111; box-shadow:0 8px 24px rgba(0,0,0,.06);
        }

        .checklist{ margin:.5rem 0 1rem; padding-left:0 }
        .checklist li{
          list-style:none; display:flex; gap:.6rem; align-items:flex-start; margin:.4rem 0;
          color:#213044;
        }
        .checklist li::before{
          content:""; width:.6rem; height:.6rem; margin-top:.45rem; flex:0 0 .6rem;
          border-radius:2px; background:var(--accent);
          box-shadow:0 0 0 3px var(--ring);
        }

        /* right-side image card */
        .render-card{
          background:var(--card); border:1px solid var(--ring);
          border-radius:16px; padding:.6rem; box-shadow:0 20px 40px rgba(0,0,0,.08);
        }
        .render-card img{ border-radius:12px }
      `}</style>

      {/* NAVBAR */}
      <header
        className="w-full fixed top-0 left-0 z-50 px-4 md:px-8 py-3 shadow-soft"
        style={{ background: 'var(--nav-bg)', color: 'var(--nav-ink)' }}
      >
        <div className="max-w-7xl mx-auto flex items-center justify-between gap-3">
          {/* Logo */}
          <div className="h-12 md:h-14 flex items-center">
            <img
              src="green-logo.png"
              alt="Assotech Windsor Group"
              className="max-h-full w-auto object-contain origin-left scale-[1.4] md:scale-[3.2]"
            />
          </div>

          {/* Desktop nav */}
          <nav className="hidden md:flex items-center gap-8 font-semibold uppercase tracking-wide">
            {['overview', 'gallery', 'brochure', 'sitemap', 'contact'].map((link) => (
              <a key={link} href={`#${link}`} className="hover:underline underline-offset-4">
                {link}
              </a>
            ))}
            <div className="text-right leading-tight">
              <div className="text-[11px] opacity-80">Call Us Today!</div>
              <div className="font-bold">+91-75661-55505</div>
            </div>
            <a
              href="https://wa.me/917566155505"
              target="_blank"
              rel="noreferrer"
              className="rounded px-4 py-2 font-bold text-white"
              style={{ background: 'var(--nav-ink)' }}
            >
              📞 Contact Us
            </a>
          </nav>

          {/* Mobile hamburger */}
          <button
            onClick={() => setMenuOpen((v) => !v)}
            className="md:hidden inline-flex items-center justify-center h-10 w-10"
            aria-label="Toggle menu"
          >
            <svg width="26" height="26" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d={menuOpen ? 'M6 18L18 6M6 6l12 12' : 'M3 6h18M3 12h18M3 18h18'}
              />
            </svg>
          </button>
        </div>

        {/* Mobile menu */}
        {menuOpen && (
          <div className="md:hidden mt-3 space-y-3 font-semibold uppercase tracking-wide">
            {['overview', 'gallery', 'brochure', 'sitemap', 'contact'].map((link) => (
              <a
                key={link}
                href={`#${link}`}
                onClick={() => setMenuOpen(false)}
                className="block"
                style={{ color: 'var(--nav-ink)' }}
              >
                {link}
              </a>
            ))}
            <div className="pt-2">
              <div className="text-[11px] opacity-80">Call Us Today!</div>
              <div className="font-bold">+91-75661-55505</div>
            </div>
            <a
              href="https://wa.me/917566155505"
              target="_blank"
              rel="noreferrer"
              className="inline-block rounded px-4 py-2 font-bold text-white"
              style={{ background: 'var(--nav-ink)' }}
            >
              📞 Contact Us
            </a>
          </div>
        )}
      </header>

      {/* PAGE CONTENT */}
      <main>
        {/* HERO */}
        <section className="relative min-h-[88vh] flex items-center justify-center pt-4 md:pt-24">
          <img src="/hero.jpg" alt="hero" className="absolute inset-0 w-full h-full object-cover" />
          <div className="absolute inset-0 bg-black/55" />
          <div className="relative text-center px-4 max-w-3xl text-white" data-aos="fade-up">
            <h1 className="text-4xl md:text-6xl font-bold mb-4" style={{ fontFamily: "'Playfair Display', serif" }}>
              Windsor Heights, Katni
            </h1>
            <p className="text-lg md:text-2xl text-gray-200">
              <Typewriter
                words={[
                  'Premium LIG & MIG Township',
                  'Under PMAY AHP – Jhinjhri, Katni',
                  'Modern Living. Government Support.',
                ]}
                loop={0}
                cursor
                cursorStyle="|"
                typeSpeed={60}
                deleteSpeed={40}
                delaySpeed={1400}
              />
            </p>
            <a
              href="#contact"
              className="inline-block mt-8 rounded px-6 py-3 font-semibold hover:opacity-90 transition text-black"
              style={{ background: 'var(--accent)' }}
            >
              Book a Site Visit
            </a>
          </div>
        </section>

        {/* OVERVIEW — WHITE */}
        <section id="overview" className="py-20 md:py-24 border-t-4 bg-white" style={{ borderColor: 'var(--accent)' }}>
          <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12 px-6 section-shell">
            {/* LEFT: Text */}
            <div data-aos="fade-right">
              <span className="eyebrow">Project Overview</span>
              <h2 className="section-title text-3xl md:text-5xl">Windsor Heights, Katni</h2>
              <div className="gold-hr" />
              <p className="lede">Luxury Meets Affordability, Backed by Government Assurance</p>

              <div className="copy space-y-5">
                <p>
                  Windsor Heights is not just another housing project — it’s a landmark government-backed
                  residential township under the <strong>PMAY (AHP)</strong> scheme, developed in partnership
                  with the Katni Urban Development Authority through the <strong>Public-Private Partnership (PPP)</strong> model.
                </p>

                <p>
                  Brought to life by <strong>Assotech Windsor LLP</strong>, a trusted developer with over
                  <strong> 20 years</strong> of real-estate excellence, this township is designed for those who
                  seek modern living, premium amenities, and long-term value.
                </p>

                <h4 className="font-semibold text-[1.05rem] mt-6">Key Highlights</h4>

                <div className="badges">
                  <span className="badge">Premium MIG & LIG Apartments</span>
                  <span className="badge">Stilt + 6 Floors</span>
                  <span className="badge">Government-Backed PPP</span>
                </div>

                <p className="font-semibold">MIG & LIG Mix</p>
                <ul className="checklist">
                  <li><span>LIG: <strong>384 units</strong> | Carpet: 51.67 sqm | Built-Up: 66.72 sqm | Super: 76.39 sqm</span></li>
                  <li><span>MIG: <strong>192 units</strong> | Carpet: 66.44 sqm | Built-Up: 75.27 sqm | Super: 89.03 sqm</span></li>
                </ul>

                <p className="font-semibold">Solid Progress, Assured Delivery</p>
                <p>
                  Several LIG and MIG blocks already have significant construction completed — including
                  <strong> LIG-1 with 6 floors plus stilt</strong> ready and <strong>MIG-1 up to the 5th floor</strong> —
                  ensuring buyers see real progress, not just promises. Work follows Annexure-I/II timelines
                  with strict penalties for delays to guarantee timely possession.
                </p>

                <p className="font-semibold">Luxury Amenities</p>
                <ul className="checklist">
                  <li>Clubhouse with indoor games, gymnasium & community hall</li>
                  <li>Landscaped gardens, kids’ play zones & walking tracks</li>
                </ul>

                <p className="font-semibold">Smart Location Advantage</p>
                <p>Prime Jhinjhri location with quick connectivity to city centers, schools, hospitals and markets.</p>

                <p className="font-semibold">Government-Backed Security</p>
                <ul className="checklist">
                  <li>PMAY (AHP) — subsidy for eligible buyers</li>
                  <li>Approvals by respective authorities ensuring transparency & compliance</li>
                </ul>
              </div>
            </div>

            {/* RIGHT: Render */}
            <div data-aos="fade-left">
              <div className="render-card">
                <img src="f+6.png" alt="Windsor Heights Render" />
              </div>
            </div>
          </div>
        </section>

        {/* WHY CHOOSE — WHITE */}
        <section className="py-16 md:py-20 border-t-4 bg-white" style={{ borderColor: 'var(--accent)' }}>
          <div className="max-w-6xl mx-auto px-6 text-center" data-aos="zoom-in">
            <h3
              className="text-2xl md:text-3xl font-bold mb-10"
              style={{ color: 'var(--accent)', fontFamily: "'Playfair Display', serif" }}
            >
              Why Choose Windsor Heights?
            </h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-8 text-gray-700">
              {[
                ['Stilt + 6 Construction', 'Earthquake-resistant design'],
                ['Prime Katni Location', 'Schools, Hospitals & Roads nearby'],
                ['Government-Backed', 'Under PMAY AHP Housing Scheme'],
                ['Affordable Luxury', 'Modern flats at low EMIs'],
              ].map(([title, desc], i) => (
                <div key={i} className="space-y-1" data-aos="fade-up" data-aos-delay={i * 120}>
                  <p className="font-semibold text-gray-900">{title}</p>
                  <p className="text-xs text-gray-600">{desc}</p>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* GALLERY — WHITE */}
        <section id="gallery" className="py-20 md:py-24 border-t-4 bg-white" style={{ borderColor: 'var(--accent)' }}>
          <div className="max-w-6xl mx-auto px-6 text-center">
            <h2
              className="text-3xl md:text-4xl font-bold mb-10"
              style={{ color: 'var(--accent)', fontFamily: "'Playfair Display', serif" }}
              data-aos="fade-down"
            >
              Gallery
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {['gallery1', 'gallery2', 'gallery3'].map((img, i) => (
                <img
                  key={img}
                  src={`/assets/${img}.jpg`}
                  alt={`Gallery ${i + 1}`}
                  className="rounded-lg"
                  data-aos="zoom-in"
                  data-aos-delay={i * 100}
                />
              ))}
            </div>
          </div>
        </section>

        {/* BROCHURE — WHITE */}
        <section id="brochure" className="py-20 md:py-24 border-t-4 bg-white text-center" style={{ borderColor: 'var(--accent)' }}>
