import React, { useEffect, useState } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { Typewriter } from 'react-simple-typewriter';

function App() {
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    AOS.init({ duration: 900, once: true, offset: 40 });
  }, []);

  return (
    <>
      {/* THEME + GLOBAL */}
      <style>{`
        :root{
          --nav-bg:#A7D3C6;     /* mint navbar background */
          --nav-ink:#0B4B3A;    /* deep forest navbar text/buttons */
          --accent:#E3B341;     /* gold accent */
          --ink:#0B4B3A;
          --body:#1d2732;
          --muted:#5b6776;
          --card:#ffffff;
          --ring:rgba(227,179,65,.25);
          --nav-h:88px;         /* header height (mobile) */
        }
        @media (min-width:768px){
          :root{ --nav-h:96px; } /* header height (desktop) */
        }

        html { scroll-behavior: smooth; }
        body, #root { background:#F6F7F8; color:#111; overflow-x:hidden; }
        img, video { max-width:100%; height:auto; }
        .shadow-soft { box-shadow: 0 6px 18px rgba(0,0,0,.08); }

        /* keep anchor jumps below fixed navbar (no global padding on main) */
        [id]{ scroll-margin-top: calc(var(--nav-h) + 10px); }

        /* --- Decorative fade band behind sections --- */
        .section-shell{ position:relative }
        .section-shell::before{
          content:"";
          position:absolute; inset:-2rem -9999px auto -9999px; height:220px;
          background:linear-gradient(180deg, rgba(227,179,65,.08), transparent 70%);
          pointer-events:none;
        }

        /* Headings / badges */
        .eyebrow{
          display:inline-block; font-size:.8rem; letter-spacing:.12em; text-transform:uppercase;
          color:var(--muted); background:rgba(227,179,65,.12); border:1px solid var(--ring);
          padding:.4rem .6rem; border-radius:999px;
        }
        .section-title{
          font-family:'Playfair Display', serif; line-height:1.1;
          color:var(--accent); font-weight:800; margin:.75rem 0 1rem;
        }
        .gold-hr{
          height:3px; width:72px; background:var(--accent); border-radius:3px; margin:.75rem 0 1.25rem;
        }
        .lede{ font-weight:600; color:var(--body); }
        .copy{ color:#223041; font-size:1.05rem; }

        .badges{ display:flex; flex-wrap:wrap; gap:.5rem .6rem; margin:.35rem 0 1rem; }
        .badge{
          background:#fff; border:1px solid var(--ring); padding:.45rem .6rem; border-radius:10px;
          font-size:.9rem; color:#111; box-shadow:0 8px 24px rgba(0,0,0,.06);
        }

        /* Standalone "About" block */
        .about-block{
          background:transparent;
          border-left:4px solid var(--accent);
          padding:.25rem 0 .25rem 1rem;
          margin: .5rem 0 1.25rem;
        }
        .about-block h5{
          margin:0 0 .4rem; font-weight:700; font-size:1rem; color:#0f172a;
        }
        .about-block p{ margin:0; color:#223041; font-size:1.05rem; }

        /* Overview content cards */
        .content-grid{
          display:grid; grid-template-columns: 1fr; gap:1rem; margin-top:.25rem;
        }
        @media(min-width:768px){
          .content-grid{ grid-template-columns: 1fr 1fr; }
        }
        .content-card{
          background:#fff; border:1px solid var(--ring); border-radius:14px;
          padding:1rem 1.1rem; box-shadow:0 10px 24px rgba(0,0,0,.06);
        }
        .content-card h5{
          font-weight:700; font-size:.95rem; color:#0f172a; margin:0 0 .5rem;
        }

        /* stat pills inside cards */
        .stat-row{ display:grid; grid-template-columns: 1fr; gap:.6rem; }
        @media(min-width:520px){
          .stat-row{ grid-template-columns: 1fr 1fr; }
        }
        .stat{
          border:1px solid var(--ring); border-radius:10px; padding:.6rem .7rem; background:#fff;
        }
        .stat .t{ font-weight:700; }
        .stat .s{ font-size:.82rem; color:#5b6776 }

        /* compact checklist columns */
        .check-cols{ display:grid; grid-template-columns: 1fr; gap:.35rem 1rem; }
        @media(min-width:640px){
          .check-cols{ grid-template-columns: 1fr 1fr; }
        }
        .check-cols li{
          list-style:none; display:flex; gap:.5rem; align-items:flex-start; color:#213044;
        }
        .check-cols li::before{
          content:""; width:.55rem; height:.55rem; margin-top:.42rem; flex:0 0 .55rem;
          border-radius:2px; background:var(--accent); box-shadow:0 0 0 3px var(--ring);
        }

        /* "fun" sparkle list for the new box */
        .fun-list{ padding-left:0; }
        .fun-list li{
          list-style:none; display:flex; gap:.5rem; align-items:flex-start; color:#213044;
        }
        .fun-list li::before{
          content:"âœ¨"; margin-top:.1rem; font-size:1rem; line-height:1;
        }

        /* image stack (right column) */
        .image-stack{ display:flex; flex-direction:column; gap:1rem; }
        .image-card{
          background:var(--card); border:1px solid var(--ring); border-radius:16px; padding:.6rem;
          box-shadow:0 20px 40px rgba(0,0,0,.08);
          transition: transform .25s ease, box-shadow .25s ease;
        }
        .image-card:hover{ transform: rotate(-.3deg) translateY(-2px); box-shadow:0 26px 60px rgba(0,0,0,.12); }
        .image-card img{ border-radius:12px; display:block; width:100%; height:auto; }
        /* Mobile: full width; Desktop: smaller second card for visual rhythm */
